    44→/// Size of the bounded channels used to communicate between tasks. The value
    45→/// is a balance between throughput and memory usage – 128 messages should be
    46→/// plenty for an interactive CLI.
    47→const CHANNEL_CAPACITY: usize = 128;
    48→
    49→pub async fn run_main(
    50→    codex_linux_sandbox_exe: Option<PathBuf>,
    51→    cli_config_overrides: CliConfigOverrides,
    52→) -> IoResult<()> {
    53→    // Install a simple subscriber so `tracing` output is visible.  Users can
    54→    // control the log level with `RUST_LOG`.
    55→    tracing_subscriber::fmt()
    56→        .with_writer(std::io::stderr)
    57→        .with_env_filter(EnvFilter::from_default_env())
    58→        .init();
    59→
    60→    // Set up channels.
    61→    let (incoming_tx, mut incoming_rx) = mpsc::channel::<JSONRPCMessage>(CHANNEL_CAPACITY);
    62→    let (outgoing_tx, mut outgoing_rx) = mpsc::unbounded_channel::<OutgoingMessage>();
    63→
    64→    // Task: read from stdin, push to `incoming_tx`.
    65→    let stdin_reader_handle = tokio::spawn({
    66→        async move {
    67→            let stdin = io::stdin();
    68→            let reader = BufReader::new(stdin);
    69→            let mut lines = reader.lines();
    70→
    71→            while let Some(line) = lines.next_line().await.unwrap_or_default() {
    72→                match serde_json::from_str::<JSONRPCMessage>(&line) {
    73→                    Ok(msg) => {
    74→                        if incoming_tx.send(msg).await.is_err() {
    75→                            // Receiver gone – nothing left to do.
    76→                            break;
    77→                        }
    78→                    }
    79→                    Err(e) => error!("Failed to deserialize JSONRPCMessage: {e}"),
    80→                }
    81→            }
    82→
    83→            debug!("stdin reader finished (EOF)");
    84→        }
    85→    });
    86→
    87→    // Parse CLI overrides once and derive the base Config eagerly so later
    88→    // components do not need to work with raw TOML values.
    89→    let cli_kv_overrides = cli_config_overrides.parse_overrides().map_err(|e| {
    90→        std::io::Error::new(
    91→            ErrorKind::InvalidInput,
    92→            format!("error parsing -c overrides: {e}"),
    93→        )
    94→    })?;
    95→    let config = Config::load_with_cli_overrides(cli_kv_overrides, ConfigOverrides::default())
    96→        .map_err(|e| {
    97→            std::io::Error::new(ErrorKind::InvalidData, format!("error loading config: {e}"))
    98→        })?;
    99→
   100→    // Task: process incoming messages.
   101→    let processor_handle = tokio::spawn({
   102→        let outgoing_message_sender = OutgoingMessageSender::new(outgoing_tx);
   103→        let mut processor = MessageProcessor::new(
   104→            outgoing_message_sender,
   105→            codex_linux_sandbox_exe,
   106→            std::sync::Arc::new(config),
   107→        );
   108→        async move {
   109→            while let Some(msg) = incoming_rx.recv().await {
   110→                match msg {
   111→                    JSONRPCMessage::Request(r) => processor.process_request(r).await,
   112→                    JSONRPCMessage::Response(r) => processor.process_response(r).await,
   113→                    JSONRPCMessage::Notification(n) => processor.process_notification(n).await,
   114→                    JSONRPCMessage::Error(e) => processor.process_error(e),
   115→                }
   116→            }
   117→
   118→            info!("processor task exited (channel closed)");
   119→        }
   120→    });
   121→
   122→    // Task: write outgoing messages to stdout.
   123→    let stdout_writer_handle = tokio::spawn(async move {
   124→        let mut stdout = io::stdout();
   125→        while let Some(outgoing_message) = outgoing_rx.recv().await {
   126→            let msg: JSONRPCMessage = outgoing_message.into();
   127→            match serde_json::to_string(&msg) {
   128→                Ok(json) => {
   129→                    if let Err(e) = stdout.write_all(json.as_bytes()).await {
   130→                        error!("Failed to write to stdout: {e}");
   131→                        break;
   132→                    }
   133→                    if let Err(e) = stdout.write_all(b"\n").await {
   134→                        error!("Failed to write newline to stdout: {e}");
   135→                        break;
   136→                    }
   137→                }
   138→                Err(e) => error!("Failed to serialize JSONRPCMessage: {e}"),
   139→            }
   140→        }
   141→
   142→        info!("stdout writer exited (channel closed)");
   143→    });
   144→
   145→    // Wait for all tasks to finish.  The typical exit path is the stdin reader
   146→    // hitting EOF which, once it drops `incoming_tx`, propagates shutdown to
   147→    // the processor and then to the stdout task.
   148→    let _ = tokio::join!(stdin_reader_handle, processor_handle, stdout_writer_handle);
   149→
   150→    Ok(())
   151→}
