    78→    pub(crate) async fn process_request(&mut self, request: JSONRPCRequest) {
    79→        if let Ok(request_json) = serde_json::to_value(request.clone())
    80→            && let Ok(codex_request) = serde_json::from_value::<ClientRequest>(request_json)
    81→        {
    82→            // If the request is a Codex request, handle it with the Codex
    83→            // message processor.
    84→            self.codex_message_processor
