    93→        let client_request = match McpClientRequest::try_from(request) {
    94→            Ok(client_request) => client_request,
    95→            Err(e) => {
    96→                tracing::warn!("Failed to convert request: {e}");
    97→                return;
    98→            }
    99→        };
