   162→        // Similar to requests, route each notification type to its own stub
   163→        // handler so additional logic can be implemented incrementally.
   164→        match server_notification {
   165→            ServerNotification::CancelledNotification(params) => {
   166→                self.handle_cancelled_notification(params).await;
   167→            }
   168→            ServerNotification::ProgressNotification(params) => {
   169→                self.handle_progress_notification(params);
