   194→    async fn handle_initialize(
   195→        &mut self,
   196→        id: RequestId,
   197→        params: <mcp_types::InitializeRequest as ModelContextProtocolRequest>::Params,
   198→    ) {
   199→        tracing::info!("initialize -> params: {:?}", params);
   200→
   201→        if self.initialized {
   202→            // Already initialised: send JSON-RPC error response.
   203→            let error = JSONRPCErrorError {
   204→                code: INVALID_REQUEST_ERROR_CODE,
   205→                message: "initialize called more than once".to_string(),
   206→                data: None,
   207→            };
   208→            self.outgoing.send_error(id, error).await;
   209→            return;
   210→        }
   211→
   212→        let client_info = params.client_info;
   213→        let name = client_info.name;
   214→        let version = client_info.version;
   215→        let user_agent_suffix = format!("{name}; {version}");
   216→        if let Ok(mut suffix) = USER_AGENT_SUFFIX.lock() {
   217→            *suffix = Some(user_agent_suffix);
   218→        }
   219→
   220→        self.initialized = true;
   221→
   222→        // Build a minimal InitializeResult. Fill with placeholders.
   223→        let result = mcp_types::InitializeResult {
   224→            capabilities: mcp_types::ServerCapabilities {
   225→                completions: None,
   226→                experimental: None,
   227→                logging: None,
   228→                prompts: None,
   229→                resources: None,
   230→                tools: Some(ServerCapabilitiesTools {
   231→                    list_changed: Some(true),
   232→                }),
   233→            },
   234→            instructions: None,
   235→            protocol_version: params.protocol_version.clone(),
   236→            server_info: mcp_types::Implementation {
   237→                name: "codex-mcp-server".to_string(),
   238→                version: env!("CARGO_PKG_VERSION").to_string(),
   239→                title: Some("Codex".to_string()),
   240→                user_agent: Some(get_codex_user_agent()),
   241→            },
   242→        };
   243→
   244→        self.send_response::<mcp_types::InitializeRequest>(id, result)
   245→            .await;
   246→    }
